#! /bin/sh

# Quelques tests que tous les compilateurs devraient passer.
# Le code est volontairement peu illisible : ce n'est pas un exemple à
# suivre pour écrire une belle base de tests.
# 
die () {    echo "\033[1;31mERREUR :\033[0m $@";    exit 1;};if [ "$GL_EVALUATION_ONGOING" = "" ]; then    PATH=$(dirname "$0")/../../../src/main/bin:"$PATH";fi;DECAC=$(cd "$(dirname "$(command -v decac)")" && pwd)/decac;[ -x "$DECAC" ] || die "commande decac non-trouvée";echo "Test de l'executable:";echo "$DECAC";decac () {    "$DECAC" "$@";};tmpdir=.minimal-test.$$;mkdir "$tmpdir" || die "Impossible de créer le répertoire de test";(    cd "$tmpdir";    mkdir source || die "Impossible de créer un répertoire dans $PWD";    echo '{println(}' > source/syntax-ko.deca;    echo '{println(x);}' > source/syntax-ok-verif-ko.deca;    echo '{println("Hello");}' > source/hello.deca;    decac -p source/hello.deca > hello-p.deca || die "Echec de la compilation d'un programme Deca simple avec -p";    [ -s hello-p.deca ] || die "decac -p n'a pas généré de sortie sur un programme Deca simple";    decac -p hello-p.deca >/dev/null || die "decac -p a produit une sortie qui n'est pas un programme Deca valide";    ! decac -p source/syntax-ko.deca > sortie.txt 2>&1 || die "decac -p n'a pas terminé avec un status différent de 0 sur un programme incorrect";    grep -qi "source/syntax-ko.deca:1:" sortie.txt || die "decac -p n'a pas affiche de message d'erreur correctement";    decac -p source/syntax-ok-verif-ko.deca > sortie.txt 2>&1 || die "decac -p a echoué sur un programme invalide mais syntaxiquement correct";    [ -s sortie.txt ] || die "decac -p n'a pas produit une sortie sur un programme invalide mais syntaxiquement correct";    ! grep -qi "source/syntax-ok-verif-ko.deca:1:" sortie.txt || die "decac -p a affiché une erreur sur un programme syntaxiquement correct";    decac -v source/hello.deca > sortie.txt 2>&1 || die "Echec de la compilation d'un programme Deca simple avec -v";    [ ! -s sortie.txt ] || die "decac -v a produit une sortie sur un fichier correct";    ! decac -v source/syntax-ok-verif-ko.deca > sortie.txt 2>&1 || die "decac -v n'a pas échoué sur une variable non-initialisée";    [ -s sortie.txt ] || die "decac -v n'a pas produit de sortie sur une variable non-initialisée";    grep -qi "source/syntax-ok-verif-ko.deca:1:" sortie.txt || die "decac -v n'a pas produit de message d'erreur sur une variable non-initialisée";    decac source/hello.deca > sortie.txt 2>&1 || die "Echec de la compilation d'un programme Deca simple";    [ ! -s sortie.txt ] || die "deca a produit une sortie sur un programme correct";    [ ! -f hello.ass ] || die "decac a généré le fichier .ass dans le répertoire courant.;Il devrait être généré au même endroit que le fichier source.";    [ -f source/hello.ass ] || die "decac n'a pas généré le fichier .ass au bon endroit";    ima source/hello.ass > sortie || die "Erreur lors de l'exécution du code généré";    echo "Hello" > attendu;    diff sortie attendu || die "L'execution du code généré pour un hello-world est incorrecte";    echo "Les fonctionalités de base semblent opérationnelles.";);status=$?;rm -fr "$tmpdir";exit "$status";